root.script:
        var lastVoteTime = {};
        function ToggleMute()
        {
        var playerId = $.GetContextPanel().GetAttributeInt( "player_id", -1 );
        if ( playerId !== -1 )
        {
        var newIsMuted = !Game.IsPlayerMuted( playerId );
        Game.SetPlayerMuted( playerId, newIsMuted );
        $.GetContextPanel().SetHasClass( "player_muted", newIsMuted );
        }
        }


        function OnGiveResourcesButton(){
        var playerPanel = $.GetContextPanel();
        var casterID = Players.GetLocalPlayer();
        var target = playerPanel.pID;
        var gold = Number(playerPanel.FindChildInLayoutFile("GoldEntry").text)
        || 0;
        var lumber =
        Number(playerPanel.FindChildInLayoutFile("LumberEntry").text) || 0;
        playerPanel.FindChildInLayoutFile("GoldEntry").text = "";
        playerPanel.FindChildInLayoutFile("LumberEntry").text = "";
        GameEvents.SendCustomGameEventToServer( "give_resources", { gold:gold,
        lumber:lumber , target:target , casterID:casterID});
        }

        function OnVoteKickButton(){
        var playerPanel = $.GetContextPanel();
        var casterID = Players.GetLocalPlayer();
        var target = playerPanel.pID;
        GameEvents.SendCustomGameEventToServer( "votekick_start", {target:target
        , casterID:casterID});
        }

        function OnVoteFlagButton(){
			var playerPanel = $.GetContextPanel();
			var casterID = Players.GetLocalPlayer();
			var target = playerPanel.pID;
			GameEvents.SendCustomGameEventToServer( "flag_start", {target:target , casterID:casterID});
			$.Msg("Flag: ");
		}var ui = GameUI.CustomUIConfig();
        function OnGiveAllResourcesButton(){
            var playerPanel = $.GetContextPanel();
            var casterID = Players.GetLocalPlayer();
            var target = playerPanel.pID;
            var gold = ui.playerGold[casterID];
            var lumber = ui.playerLumber[casterID];
            playerPanel.FindChildInLayoutFile("GoldEntry").text = "";
            playerPanel.FindChildInLayoutFile("LumberEntry").text = "";
            GameEvents.SendCustomGameEventToServer( "give_resources", { gold:gold,
            lumber:lumber , target:target , casterID:casterID});
        }

        function OnGiveAllGoldButton(){
            var playerPanel = $.GetContextPanel();
            var casterID = Players.GetLocalPlayer();
            var target = playerPanel.pID;
            var gold = ui.playerGold[casterID];
            var lumber = ui.playerLumber[casterID];
            playerPanel.FindChildInLayoutFile("GoldEntry").text = "";
            playerPanel.FindChildInLayoutFile("LumberEntry").text = "";
            GameEvents.SendCustomGameEventToServer( "give_resources", {
                gold,
                lumber: 0,
                target,
                casterID
            });
        }
        function OnGiveAllLumberButton(){
            var playerPanel = $.GetContextPanel();
            var casterID = Players.GetLocalPlayer();
            var target = playerPanel.pID;
            var gold = ui.playerGold[casterID];
            var lumber = ui.playerLumber[casterID];
            playerPanel.FindChildInLayoutFile("GoldEntry").text = "";
            playerPanel.FindChildInLayoutFile("LumberEntry").text = "";
            GameEvents.SendCustomGameEventToServer( "give_resources", {
                gold: 0,
                lumber,
                target,
                casterID
            });
        }
        (function()
